pub fn get_floor() -> i32 {
    FLOOR_DIRECTIONS
        .chars()
        .map(|c| match c {
            '(' => 1,
            ')' => -1,
            _ => unreachable!(),
        })
        .sum()
}
fn first_negative_floor() -> usize {
    let mut current_floor = 0;
    for (index, dir) in FLOOR_DIRECTIONS.char_indices() {
        current_floor = current_floor
            + match dir {
                '(' => 1,
                ')' => -1,
                _ => unreachable!(),
            };
        if current_floor == -1 {
            return index + 1;
        }
    }
    unimplemented!("failed to reach -1 floor with the current input")
}
fn main() {
    dbg!(get_floor(), first_negative_floor());
}
